    //         //     const newData = [...prev, data.children];
    //         //     console.log(newData);
    //         //     return newData;
    //         // })
    //     }
    // }
    if (data.to) {
        Comp = 'Link'
    }

    return (
        <Tippy
            placement="bottom-end"
            interactive
            render={attrs => (
                <div className="box" tabIndex="-1" {...attrs}>
                    <div className={styles.wrapper}>
                        {/* {isChildren && <HeaderOfMenu />} */}
                        {
                            current.data.map(
                                (item, index) => {
                                    const isParent = !!item.children;
                                    if(isParent) {
                                        handleClick = () => {
                                            setHistory(prev => setHistory(
                                                [...prev, data.children]
                                            ))
                                        }
                                    }
                                    return <MenuPopped data={item} key={index} onClick={handleClick} />
                                }
                            )
                        }

                    </div>
                </div>
            )}
        >
            {children}
        </Tippy>
        // <div>
        //     {
        //         data.map(
        //             (item, index) => {
        //                 <MenuPopped data={item}>{children}</MenuPopped>
        //             }
        //         )
