import React, { useState } from "react";

const useDebounce = (value, timeDelay) => {
    const [currentValue, setCurrentValue] = useState(value);

    setTimeout(
        ()=> {
            setCurrentValue(value);
            console.log(currentValue);
        }, timeDelay
    )

    return currentValue;
}

export { useDebounce };
